var sites = {};

var x = data.series[0].fields[0].values;
var y = data.series[0].fields[1].values;
var names = data.series[0].fields[2].values;

names.forEach(site => {
  sites[site] = {
    x: [],
    y: [],
    name: site,
    line: {
      width: 1
    }
  }
}); 
